import React from 'react';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';

const Cart = () => {
  const { cart, removeFromCart, updateQuantity, getTotalPrice, clearCart } = useCart();

  const handleBuyNow = () => {
    // Create dramatic effects container
    const effectsContainer = document.createElement('div');
    effectsContainer.className = 'fixed inset-0 z-50 pointer-events-none';
    effectsContainer.innerHTML = `
      <style>
        @keyframes shake {
          0%, 100% { transform: translateX(0) translateY(0); }
          10%, 30%, 50%, 70%, 90% { transform: translateX(-15px) translateY(-5px); }
          20%, 40%, 60%, 80% { transform: translateX(15px) translateY(5px); }
        }
        
        @keyframes bulletFire {
          0% { transform: scale(0) rotate(0deg); opacity: 0; }
          50% { transform: scale(2) rotate(180deg); opacity: 1; }
          100% { transform: scale(4) rotate(360deg); opacity: 0; }
        }
        
        @keyframes crackSpread {
          0% { opacity: 0; transform: scale(0); }
          50% { opacity: 1; transform: scale(1); }
          100% { opacity: 0.9; transform: scale(1.3); }
        }
        
        @keyframes bloodDrip {
          0% { opacity: 0; transform: translateY(-100px) scale(0); }
          100% { opacity: 1; transform: translateY(0) scale(1); }
        }
        
        @keyframes fadeOut {
          0% { opacity: 1; }
          100% { opacity: 0; }
        }
        
        .screen-shake {
          animation: shake 0.8s ease-in-out;
        }
        
        .bullet-hole {
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          width: 30px;
          height: 30px;
          background: radial-gradient(circle, #ff0000 0%, #8b0000 30%, #000 70%, transparent 100%);
          border-radius: 50%;
          box-shadow: 0 0 20px #ff0000, 0 0 40px #ff0000;
          animation: bulletFire 0.6s ease-out;
        }
        
        .crack {
          position: absolute;
          background: linear-gradient(45deg, transparent 30%, #000 50%, transparent 70%);
          box-shadow: 0 0 10px #000;
          animation: crackSpread 1.2s ease-out forwards;
        }
        
        .blood-stain {
          position: absolute;
          background: radial-gradient(ellipse, #8b0000 0%, #ff0000 20%, transparent 70%);
          animation: bloodDrip 1.5s ease-out forwards;
          filter: blur(1px);
        }
        
        .effects-overlay {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: rgba(0, 0, 0, 0.3);
          pointer-events: none;
          z-index: 9999;
        }
      </style>
    `;
    document.body.appendChild(effectsContainer);

    // Screen shake effect
    document.body.classList.add('screen-shake');

    // Create effects overlay
    const overlay = document.createElement('div');
    overlay.className = 'effects-overlay';
    effectsContainer.appendChild(overlay);

    // Create bullet hole at center
    const bulletHole = document.createElement('div');
    bulletHole.className = 'bullet-hole';
    effectsContainer.appendChild(bulletHole);

    // Create crack effects spreading from center
    const crackPositions = [
      { width: '200px', height: '3px', angle: 0 },
      { width: '200px', height: '3px', angle: 45 },
      { width: '200px', height: '3px', angle: 90 },
      { width: '200px', height: '3px', angle: 135 },
      { width: '150px', height: '2px', angle: 22.5 },
      { width: '150px', height: '2px', angle: 67.5 },
      { width: '150px', height: '2px', angle: 112.5 },
      { width: '150px', height: '2px', angle: 157.5 }
    ];

    crackPositions.forEach((crack, index) => {
      const crackElement = document.createElement('div');
      crackElement.className = 'crack';
      crackElement.style.position = 'absolute';
      crackElement.style.top = '50%';
      crackElement.style.left = '50%';
      crackElement.style.width = crack.width;
      crackElement.style.height = crack.height;
      crackElement.style.transformOrigin = 'center';
      crackElement.style.transform = `translate(-50%, -50%) rotate(${crack.angle}deg)`;
      crackElement.style.animationDelay = `${index * 0.1}s`;
      effectsContainer.appendChild(crackElement);
    });

    // Create blood stains
    const bloodPositions = [
      { top: '20%', left: '30%', size: '40px' },
      { top: '30%', left: '70%', size: '50px' },
      { top: '60%', left: '20%', size: '35px' },
      { top: '70%', left: '80%', size: '45px' },
      { top: '40%', left: '50%', size: '60px' },
      { top: '80%', left: '40%', size: '40px' },
      { top: '15%', left: '60%', size: '30px' },
      { top: '85%', left: '15%', size: '55px' },
      { top: '50%', left: '85%', size: '35px' },
      { top: '25%', left: '10%', size: '45px' }
    ];

    bloodPositions.forEach((blood, index) => {
      const bloodElement = document.createElement('div');
      bloodElement.className = 'blood-stain';
      bloodElement.style.position = 'absolute';
      bloodElement.style.width = blood.size;
      bloodElement.style.height = blood.size;
      bloodElement.style.top = blood.top;
      bloodElement.style.left = blood.left;
      bloodElement.style.borderRadius = '50%';
      bloodElement.style.animationDelay = `${0.3 + index * 0.1}s`;
      effectsContainer.appendChild(bloodElement);
    });

    // Play gunshot sound using Web Audio API
    const audioContext = new (window.AudioContext || window.webkitAudioContext)();
    const oscillator = audioContext.createOscillator();
    const gainNode = audioContext.createGain();
    
    oscillator.connect(gainNode);
    gainNode.connect(audioContext.destination);
    
    oscillator.frequency.setValueAtTime(800, audioContext.currentTime);
    oscillator.type = 'square';
    gainNode.gain.setValueAtTime(0.3, audioContext.currentTime);
    gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.2);
    
    oscillator.start(audioContext.currentTime);
    oscillator.stop(audioContext.currentTime + 0.2);

    // Clean up effects after animation
    setTimeout(() => {
      document.body.classList.remove('screen-shake');
      effectsContainer.style.animation = 'fadeOut 2s forwards';
      
      setTimeout(() => {
        effectsContainer.remove();
        alert('💀 PURCHASE COMPLETE 💀\n\nYour weapons have been dispatched to your location.\n\n⚠️ This is a simulation - no actual weapons will be delivered.');
        clearCart();
      }, 2000);
    }, 3000);
  };

  return (
    <div className="min-h-screen bg-black text-white">
      <div className="max-w-7xl mx-auto px-4 py-12">
        <h1 className="text-4xl font-bold text-center mb-8">
          <span className="bg-gradient-to-r from-red-500 to-red-700 bg-clip-text text-transparent">
            💀 DEATH DEALER CART 💀
          </span>
        </h1>

        {cart.length === 0 ? (
          <div className="text-center py-20">
            <h2 className="text-2xl text-gray-400 mb-4">Your cart is empty</h2>
            <p className="text-gray-500 mb-8">Add some weapons to get started</p>
            <Link 
              to="/home"
              className="bg-red-600 hover:bg-red-700 text-white px-6 py-3 rounded-lg font-bold"
            >
              Browse Weapons
            </Link>
          </div>
        ) : (
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Cart Items */}
            <div className="lg:col-span-2">
              <h2 className="text-2xl font-bold text-red-400 mb-6">Your Arsenal</h2>
              <div className="space-y-4">
                {cart.map((item, index) => (
                  <div key={index} className="bg-gray-900 border border-red-800 rounded-lg p-4 flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <img 
                        src={item.image} 
                        alt={item.name}
                        className="w-16 h-16 object-cover rounded"
                        onError={(e) => {
                          e.target.src = `https://via.placeholder.com/64x64/1a1a1a/ff0000?text=${encodeURIComponent(item.name)}`;
                        }}
                      />
                      <div>
                        <h3 className="text-lg font-bold text-red-400">{item.name}</h3>
                        <p className="text-sm text-gray-400">{item.type}</p>
                        <p className="text-sm text-gray-400">{item.caliber}</p>
                      </div>
                    </div>
                    
                    <div className="flex items-center space-x-4">
                      <div className="flex items-center space-x-2">
                        <button 
                          onClick={() => updateQuantity(item.name, (item.quantity || 1) - 1)}
                          className="bg-gray-700 hover:bg-gray-600 text-white px-2 py-1 rounded"
                        >
                          -
                        </button>
                        <span className="text-white w-8 text-center">{item.quantity || 1}</span>
                        <button 
                          onClick={() => updateQuantity(item.name, (item.quantity || 1) + 1)}
                          className="bg-gray-700 hover:bg-gray-600 text-white px-2 py-1 rounded"
                        >
                          +
                        </button>
                      </div>
                      
                      <span className="text-xl font-bold text-red-500">
                        ${(parseFloat(item.price.replace('$', '').replace(',', '')) * (item.quantity || 1)).toLocaleString()}
                      </span>
                      
                      <button 
                        onClick={() => removeFromCart(item.name)}
                        className="text-red-500 hover:text-red-400"
                      >
                        ✕
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Order Summary */}
            <div className="bg-gray-900 border border-red-800 rounded-lg p-6 h-fit">
              <h3 className="text-xl font-bold text-red-400 mb-4">Order Summary</h3>
              
              <div className="space-y-2 mb-4">
                <div className="flex justify-between">
                  <span className="text-gray-400">Subtotal</span>
                  <span className="text-white">₹{getTotalPrice().toLocaleString()}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-400">Shipping</span>
                  <span className="text-green-400">FREE</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-400">CGST (9%)</span>
                  <span className="text-white">₹{(getTotalPrice() * 0.09).toLocaleString()}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-400">SGST (9%)</span>
                  <span className="text-white">₹{(getTotalPrice() * 0.09).toLocaleString()}</span>
                </div>
                <div className="border-t border-red-800 pt-2">
                  <div className="flex justify-between text-xl font-bold">
                    <span className="text-red-400">Total</span>
                    <span className="text-red-500">₹{(getTotalPrice() * 1.18).toLocaleString()}</span>
                  </div>
                </div>
              </div>

              <button 
                onClick={handleBuyNow}
                className="w-full bg-red-600 hover:bg-red-700 text-white py-3 rounded-lg font-bold text-xl transform hover:scale-105 transition-all duration-200"
              >
                💀 BUY NOW 💀
              </button>

              <button 
                onClick={clearCart}
                className="w-full mt-4 bg-gray-700 hover:bg-gray-600 text-white py-2 rounded-lg"
              >
                Clear Cart
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
