import { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const success = await login(username, password);
      if (success) {
        navigate("/home");
      } else {
        setError("ACCESS DENIED - INVALID TEA CREDENTIALS");
      }
    } catch (err) {
      setError("TEA REJECTS YOUR PRESENCE");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-green-100 relative overflow-hidden">
      <form onSubmit={handleSubmit} className="relative bg-white border-2 border-green-600 p-10 rounded-lg shadow-2xl w-full max-w-md">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-green-600 mb-2">üçµ MANOR TEA üçµ</h1>
          <h2 className="text-xl text-green-500">TEA ACCESS</h2>
          <p className="text-gray-500 text-sm mt-2">WELCOME TO THE TEA DOMAIN</p>
        </div>
        
        {error && (
          <div className="bg-red-100 border border-red-600 text-red-600 p-4 rounded mb-6 text-center">
            ‚ö†Ô∏è {error} ‚ö†Ô∏è
          </div>
        )}
        
        <div className="mb-6">
          <label htmlFor="username" className="block text-green-600 mb-3 font-bold uppercase tracking-wider">
            Tea Username
          </label>
          <input
            id="username"
            type="text"
            placeholder="Enter your name..."
            className="block w-full p-4 rounded border-2 border-green-600 focus:border-green-500 focus:outline-none"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        
        <div className="mb-8">
          <label htmlFor="password" className="block text-green-600 mb-3 font-bold uppercase tracking-wider">
            Tea Password
          </label>
          <input
            id="password"
            type="password"
            placeholder="Enter your password..."
            className="block w-full p-4 rounded border-2 border-green-600 focus:border-green-500 focus:outline-none"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        
        <button 
          type="submit"
          disabled={loading}
          className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-4 px-6 rounded transition-all duration-300"
        >
          {loading ? "Loading..." : "üå± ENTER THE TEA DOMAIN üå±"}
        </button>
        
        <div className="text-center mt-6">
          <p className="text-gray-600 text-xs">
            "Only tea lovers may enter the tea domain"
          </p>
        </div>
      </form>
    </div>
  );
}
