// src/pages/CartEnhanced.jsx
import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

export default function Cart() {
  const [cart, setCart] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showEffects, setShowEffects] = useState(false);
  const navigate = useNavigate();

  // Load cart from localStorage on component mount
  useEffect(() => {
    const savedCart = localStorage.getItem('deathDealerCart');
    if (savedCart) {
      setCart(JSON.parse(savedCart));
    }
  }, []);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('deathDealerCart', JSON.stringify(cart));
  }, [cart]);

  // Calculate total
  const calculateTotal = () => {
    return cart.reduce((total, item) => {
      const price = parseInt(item.price.replace('$', '').replace(',', ''));
      return total + price;
    }, 0);
  };

  // Remove item from cart
  const removeFromCart = (index) => {
    const newCart = cart.filter((_, i) => i !== index);
    setCart(newCart);
  };

  // Handle buy now with dramatic effects
  const handleBuyNow = async () => {
    setIsLoading(true);
    setShowEffects(true);
    
    // Play gunshot sound
    const audio = new Audio('https://www.soundjay.com/misc/sounds/gun-gunshot-01.mp3');
    audio.play().catch(() => {});
    
    // Add shake effect to body
    document.body.style.animation = 'shake 0.5s';
    
    // After effects complete, navigate
    setTimeout(() => {
      setCart([]);
      localStorage.removeItem('deathDealerCart');
      navigate('/');
    }, 3000);
  };

  // Clear entire cart
  const clearCart = () => {
    setCart([]);
  };

  if (cart.length === 0) {
    return (
      <div className="min-h-screen bg-black text-white flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-4xl font-bold text-red-500 mb-4">Your Cart is Empty</h1>
          <p className="text-gray-400 mb-8">Add some deadly weapons to get started!</p>
          <Link 
            to="/" 
            className="bg-red-600 hover:bg-red-700 text-white px-6 py-3 rounded font-bold"
          >
            Continue Shopping
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-black text-white relative">
      {/* CSS for effects */}
      <style jsx>{`
        @keyframes shake {
          0%, 100% { transform: translateX(0); }
          10%, 30%, 50%, 70%, 90% { transform: translateX(-10px); }
          20%, 40%, 60%, 80% { transform: translateX(10px); }
        }
        
        @keyframes bloodDrip {
          0% { transform: translateY(-100vh); opacity: 0; }
          100% { transform: translateY(0); opacity: 0.8; }
        }
        
        @keyframes screenCrack {
          0% { transform: scale(1); opacity: 0; }
          50% { transform: scale(1.02); opacity: 0.5; }
          100% { transform: scale(1); opacity: 1; }
        }
        
        .blood-stain {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: radial-gradient(circle at 50% 50%, rgba(139, 0, 0, 0.3) 0%, transparent 70%);
          animation: bloodDrip 1s ease-in;
          z-index: 1000;
          pointer-events: none;
        }
        
        .screen-crack {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: 
            linear-gradient(45deg, transparent 48%, rgba(255, 255, 255, 0.1) 49%, rgba(255, 255, 255, 0.1) 51%, transparent 52%),
            linear-gradient(-45deg, transparent 48%, rgba(255, 255, 255, 0.1) 49%, rgba(255, 255, 255, 0.1) 51%, transparent 52%);
          animation: screenCrack 0.5s ease-in;
          z-index: 999;
          pointer-events: none;
        }
        
        .gun-flash {
          position: fixed;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          width: 100px;
          height: 100px;
          background: radial-gradient(circle, rgba(255, 255, 0, 0.8) 0%, transparent 70%);
          border-radius: 50%;
          animation: flash 0.1s ease-out;
          z-index: 1001;
          pointer-events: none;
        }
        
        @keyframes flash {
          0% { transform: translate(-50%, -50%) scale(0); opacity: 1; }
          100% { transform: translate(-50%, -50%) scale(2); opacity: 0; }
        }
      `}</style>

      {/* Effects overlay */}
      {showEffects && (
        <>
          <div className="gun-flash"></div>
          <div className="screen-crack"></div>
          <div className="blood-stain"></div>
          
          {/* Additional blood splatters */}
          <div className="fixed top-10 left-10 w-8 h-8 bg-red-600 rounded-full opacity-70" style={{animation: 'bloodDrip 1.5s ease-in'}}></div>
          <div className="fixed top-20 right-20 w-6 h-6 bg-red-700 rounded-full opacity-60" style={{animation: 'bloodDrip 1.2s ease-in'}}></div>
          <div className="fixed bottom-20 left-1/4 w-10 h-10 bg-red-500 rounded-full opacity-50" style={{animation: 'bloodDrip 1.8s ease-in'}}></div>
          <div className="fixed top-1/3 right-1/3 w-4 h-4 bg-red-800 rounded-full opacity-80" style={{animation: 'bloodDrip 1.3s ease-in'}}></div>
        </>
      )}

      {/* Hero Section */}
      <section className="relative h-64 flex items-center justify-center">
        <div className="absolute inset-0 bg-gradient-to-b from-black via-red-900/20 to-black" />
        <div className="relative z-10 text-center">
          <h1 className="text-5xl font-black mb-4">
            <span className="bg-gradient-to-r from-red-500 to-red-700 bg-clip-text text-transparent">
              SHOPPING CART
            </span>
          </h1>
          <p className="text-xl text-gray-300">
            Your arsenal of destruction awaits
          </p>
        </div>
      </section>

      {/* Main Content */}
      <section className="py-12 px-4">
        <div className="max-w-7xl mx-auto">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Cart Items */}
            <div className="lg:col-span-2">
              <h2 className="text-2xl font-bold text-red-400 mb-6">
                Cart Items ({cart.length})
              </h2>
              
              <div className="space-y-4">
                {cart.map((item, index) => (
                  <div key={index} className="bg-gray-900 border border-red-800 rounded-lg p-4">
                    <div className="flex items-center space-x-4">
                      <img 
                        src={item.image} 
                        alt={item.name}
                        className="w-20 h-20 object-cover rounded"
                        onError={(e) => {
                          e.target.src = `https://via.placeholder.com/80x80/1a1a1a/ff0000?text=${encodeURIComponent(item.name)}`;
                        }}
                      />
                      
                      <div className="flex-1">
                        <h3 className="text-lg font-bold text-red-400">{item.name}</h3>
                        <p className="text-sm text-gray-400">{item.type}</p>
                        <p className="text-sm text-gray-400">Caliber: {item.caliber}</p>
                      </div>
                      
                      <div className="text-right">
                        <p className="text-xl font-bold text-red-500">{item.price}</p>
                        <button 
                          onClick={() => removeFromCart(index)}
                          className="text-red-400 hover:text-red-300 text-sm mt-2"
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Order Summary */}
            <div className="lg:col-span-1">
              <div className="bg-gray-900 border border-red-800 rounded-lg p-6 sticky top-4">
                <h3 className="text-xl font-bold text-red-400 mb-4">Order Summary</h3>
                
                <div className="space-y-3 mb-6">
                  {cart.map((item, index) => (
                    <div key={index} className="flex justify-between text-sm">
                      <span className="text-gray-300">{item.name}</span>
                      <span className="text-red-400">{item.price}</span>
                    </div>
                  ))}
                </div>
                
                <div className="border-t border-red-800 pt-4">
                  <div className="flex justify-between text-lg font-bold">
                    <span className="text-white">Total</span>
                    <span className="text-red-500">${calculateTotal().toLocaleString()}</span>
                  </div>
                </div>
                
                <div className="mt-6 space-y-3">
                  <button 
                    onClick={handleBuyNow}
                    disabled={isLoading}
                    className="w-full bg-red-600 hover:bg-red-700 disabled:bg-red-900 text-white py-3 rounded font-bold transition-colors text-lg"
                  >
                    {isLoading ? 'üíÄ FIRING...' : 'üíÄ BUY NOW üíÄ'}
                  </button>
                  
                  <button 
                    onClick={clearCart}
                    className="w-full bg-gray-700 hover:bg-gray-600 text-white py-2 rounded"
                  >
                    Clear Cart
                  </button>
                  
                  <Link 
                    to="/"
                    className="w-full bg-gray-800 hover:bg-gray-700 text-white py-2 rounded text-center block"
                  >
                    Continue Shopping
                  </Link>
                </div>
                
                <div className="mt-4 text-xs text-gray-500 text-center">
                  <p>‚ö†Ô∏è All sales are final</p>
                  <p>Weapons are for demonstration purposes</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="py-12 px-4 bg-gray-900/50 border-t border-red-800 mt-12">
        <div className="max-w-7xl mx-auto text-center">
          <p className="text-gray-400 mb-4">
            üíÄ Death Dealer Arsenal - Your Trusted Source for Virtual Destruction üíÄ
          </p>
          <p className="text-red-500 font-bold">
            Thank you for your purchase!
          </p>
        </div>
      </footer>
    </div>
  );
}
