import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';

// Animated background component
const AnimatedBackground = () => {
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    const newParticles = Array(50).fill().map((_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      size: Math.random() * 4 + 1,
      duration: Math.random() * 20 + 10,
      delay: Math.random() * 5
    }));
    setParticles(newParticles);
  }, []);

  return (
    <div className="fixed inset-0 overflow-hidden pointer-events-none">
      {particles.map(particle => (
        <div
          key={particle.id}
          className="absolute bg-green-500 rounded-full opacity-20"
          style={{
            left: `${particle.x}%`,
            top: `${particle.y}%`,
            width: `${particle.size}px`,
            height: `${particle.size}px`,
            animation: `float ${particle.duration}s ${particle.delay}s infinite ease-in-out`
          }}
        />
      ))}
    </div>
  );
};

// Home component
export default function Home() {
  return (
    <div className="min-h-screen bg-f5c6a0 text-black relative overflow-hidden">
      <AnimatedBackground />
      
      {/* Hero Section */}
      <section className="relative h-screen flex items-center justify-center overflow-hidden">
        <div className="absolute inset-0 w-full h-full">
          <img 
            src="/images/tea-hero.jpg" 
            alt="Tea"
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 bg-black/60" />
        </div>
        
        <div className="relative z-10 text-center px-4">
          <h1 className="text-6xl md:text-8xl font-black mb-4 animate-pulse">
            <span className="bg-gradient-to-r from-green-500 to-green-700 bg-clip-text text-transparent">
              MANOR TEA
            </span>
          </h1>
          <h2 className="text-2xl md:text-4xl font-bold text-green-400 mb-8">
            The Tea of Your Morning
          </h2>
          <p className="text-xl text-gray-300 mb-12 max-w-2xl mx-auto">
            Discover the finest selection of teas, crafted for your enjoyment.
          </p>
          
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Link 
              to="/products" 
              className="bg-green-600 hover:bg-green-700 text-white px-8 py-4 rounded-lg font-bold text-lg transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-green-500/50"
            >
              Browse Teas
            </Link>
            <Link 
              to="/our-team" 
              className="border-2 border-green-600 text-green-400 hover:bg-green-600 hover:text-white px-8 py-4 rounded-lg font-bold text-lg transform hover:scale-105 transition-all duration-300"
            >
              Our Story
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
}
